    }

    render() {
        const options = [];

        let annee = 2023;
        while (annee >= 1991) {
            var obj = {};

            obj['value'] = annee;
            obj['selected'] = this.state.montant.getYear() === annee;
            options.push(obj);
            annee--;
        }

        return (
            <div>
                <p>Choisissez l'ann√©e du calcul :</p>
                <select>
                    {options.map(({ value, selected }, index) => <option value={value} selected={selected} >{value}</option>)}
                </select>
                <p>Entrez le montant avant taxe :</p>
                <input onChange={this.updateState} value={this.state.montant.getMontantSansTaxe}></input>
                <p>TPS :</p>
                <textarea readonly value={this.state.montant.getTPS}> $</textarea>
                <p>TVQ :</p>
                <textarea readonly value={this.state.montant.getTVQ}> $</textarea>
                <p>Montant Total :</p>
                <textarea readonly value={this.state.montant.getMontantAvecTaxe}> $</textarea>
            </div>
        )
    }
}
