import React, {Component} from 'react';
import MontantAvecTaxes from '../util/MontantAvecTaxes';

export default class TaxeQC extends Component {
    
    state = {
        montant : new MontantAvecTaxes(2023, 0)
    }

    changeMontant = (e) => {
        let montantSansTaxe = parseInt(e.target.value);
        let year = this.state.montant.getYear();
        const mont = new MontantAvecTaxes(year, montantSansTaxe);
        this.setState(() => ({
            montant: mont
        }));
    }

    changeAnnee = (e) => {
        let year = parseInt(e.target.value);
        let montantSansTaxe = this.state.montant.getMontantSansTaxe();
        const mont = new MontantAvecTaxes(year, montantSansTaxe);
        this.setState(() => ({
            montant: mont
        }));
    }

    render() {
        const options = [];

        let annee = 2023;
        while (annee >= 1991) {
            var obj = {};

            obj['value'] = annee;
            obj['selected'] = this.state.montant.getYear() === annee;
            options.push(obj);
            annee--;
        }

        return (
            <div>
                <p>Choisissez l'ann√©e du calcul :</p>
                <select id='annee' onChange={this.changeAnnee}>
                    {options.map(({ value, selected }, index) => <option value={value} selected={selected} >{value}</option>)}
                </select>
                <p>Entrez le montant avant taxe :</p>
                <input id='montant' onChange={this.changeMontant} value={parseInt(this.state.montant.getMontantSansTaxe).toString}></input>
                <p>TPS :</p>
                <textarea readOnly value={this.state.montant.getTPS}> $</textarea>
                <p>TVQ :</p>
                <textarea readOnly value={this.state.montant.getTVQ}> $</textarea>
                <p>Montant Total :</p>
                <textarea readOnly value={this.state.montant.getMontantAvecTaxe}> $</textarea>
            </div>
        )
    }
}
